import Link from 'next/link';
import { useRouter } from 'next/router';

import RecipeData from '../static/RecipeData';
import IngredientData from '../static/IngredientData';

import BaseContainer from '../components/BaseContainer';
import Header from '../components/Header';

const Ingredient = (props) => (
	<div>{props.ingredient.title}</div>
)

export default function Recipe() {
	const router = useRouter();

	console.log(RecipeData.recipes)
	console.log(router.query.id)
	console.log(RecipeData.recipes[router.query.id])
	var title = RecipeData.recipes[router.query.id].title;
	var ingredients = RecipeData.recipes[router.query.id].ingredients;
	ingredients.forEach(function(ingredient_id, i) {
		ingredients[i].ingredient = IngredientData[ingredient_id];
	})

	return (
		<BaseContainer>
			<Header />

			<p>{title}</p>

			<p>Ingredients:</p>
			<div>
				{ingredients.map(ingredient => (
					<Ingredient ingredient={ingredient} />
				))}
			</div>

			<button>Next</button>
		</BaseContainer>
	);
}
