import Error from 'next/error'
import Link from 'next/link';
import { useRouter } from 'next/router';

import RecipeData from '../data/RecipeData';
import IngredientData from '../data/IngredientData';

import BaseContainer from '../components/BaseContainer';
import Header from '../components/Header';

const PrevStep = function(props) {
	if (props.page) {
		return (
			<Link href={`/recipe?id=${props.id}&page=${props.page}`}>
				<a>Previous</a>
			</Link>
		)
	} else {
		return (<div></div>);
	}
}

const NextStep = function(props) {
	if (props.recipe && props.recipe.steps && props.page <= props.recipe.steps.length) {
		return (
			<Link href={`/recipe?id=${props.id}&page=${props.page}`}>
				<a>Next</a>
			</Link>
		)
	} else {
		return (<div></div>);
	}
}

const IngredientListed = function(props) {
	if (props.ingredient && props.ingredient.ingredient) {
		return (
			<div>{props.ingredient.amount} {props.ingredient.measurement} {props.ingredient.ingredient.name}</div>
		)
	} else {
		return (<div></div>);
	}
}

const StepsDisplay = function(props) {
	if (props.page == 1) {
		return (
			<div>
				<p>Ingredients:</p>
		    	{props.ingredients.map(ingredient => (
		  			<IngredientListed ingredient={ingredient} />
		  		))}
		    </div>
		)
	} else {
		return (
			<div>
				<p>Step :</p>
		    	{props.ingredients.map(ingredient => (
		  			<IngredientListed ingredient={ingredient} />
		  		))}
		    </div>
		)
	}
}



export default function RecipeView() {
	const router = useRouter();

	if (!router.query.id)
		return <Error status={404} />

	var page = router.query.page;
	if (!page)
		page = 1;

	page = parseInt(page);

	var prev_page;
	if (page > 1)
		prev_page = page-1;

	var recipe = RecipeData.recipes[router.query.id];
	var title = recipe.title;
	var ingredients = recipe.ingredients;

	ingredients && ingredients.forEach(function(ingredient, i) {
		ingredients[i].ingredient = IngredientData[ingredient.ingredient];
	})

	ingredients && ingredients.sort(function(a, b) {
		if (!a.ingredient)
			return -1;

		if (!b.ingredient)
			return 1;

		if (a.ingredient.name.toLowerCase() > b.ingredient.name.toLowerCase())
			return 1;
		else
			return -1;
	})

	return (
		<BaseContainer>
			<Header />

			<p>{title}</p>

			<StepsDisplay ingredients={ingredients} />

			<PrevStep id={router.query.id} page={prev_page} recipe={recipe} />
			<NextStep id={router.query.id} page={page+1} recipe={recipe} />
		</BaseContainer>
	);
}
