import Error from 'next/error'
import Link from 'next/link';
import { useRouter } from 'next/router';

import RecipeData from '../static/RecipeData';
import IngredientData from '../static/IngredientData';

import BaseContainer from '../components/BaseContainer';
import Header from '../components/Header';

const Ingredient = (props) => (
	<div>{props.ingredient.ingredient.name}</div>
)

export default function Recipe() {
	const router = useRouter();

	if (!router.query.id)
		return <Error status={404} />

	var recipe = RecipeData.recipes[router.query.id];
	var title = recipe.title;
	var ingredients = recipe.ingredients;

	ingredients.forEach(function(ingredient, i) {
		ingredients[i].ingredient = IngredientData[ingredient.ingredient];
	})

	return (
		<BaseContainer>
			<Header />

			<p>{title}</p>

			<p>Ingredients:</p>
			<div>
				{ingredients.map(ingredient => (
					<Ingredient ingredient={ingredient} />
				))}
			</div>

			<button>Next</button>
		</BaseContainer>
	);
}
